Item.method_code:
const propertyName = "classification";
const currentItemNode = document.thisItem.node;
const classification = currentItemNode.selectSingleNode(propertyName);
const newClassification = getFieldComponentByName(propertyName).getValue();

if (classification && classification.textContent !== newClassification) {
	classification.textContent = newClassification;
}

if (parent.reShowCurrentForm) {
	parent.reShowCurrentForm();
}
