Item.method_code:
var topWindow = aras.getMostTopWindowWithAras(window);
var isChecked = topWindow.aras.getPreferenceItemProperty('Core_GlobalLayout', null, 'core_show_labels') === 'true';
if (topWindow.work.activeToolbar) {
	topWindow.work.activeToolbar.showLabels(isChecked);
}
return {'cui_checked': isChecked};
