Item.method_code:
if (inArgs.isReinit) {
	var topWindow = aras.getMostTopWindowWithAras(window);
	var workerFrame = topWindow.work;
	if (workerFrame && workerFrame.grid && workerFrame.grid.getSelectedId()) {
		return {'cui_disabled': false};
	} else {
		return {'cui_disabled': true};
	}
}
return {};
