Item.method_code:
const itemTypeId = options.currentTarget.itemTypeId;
const targetTab = options.getItemTypeByItemTypeId(itemTypeId);
if (!itemTypeId || !targetTab) {
	return Promise.resolve();
}

const id = targetTab.id;
const removedTab = options.unpinItemType(id);

return options.favorites.delete(id).catch(function() {options.pinItemType(id, removedTab);});
