Item.method_code:
Innovator inn = this.getInnovator();

Item agent = inn.newItem("ES_Agent", "get");
agent.setAttribute("select", "id");
agent = agent.apply();

if (!agent.isError()) {
	if (agent.getItemCount() > 0) {
		return inn.newError(CCO.ErrorLookup.Lookup("ES_ForbiddenMoreThanOneAgent"));
	}
}
return this;
