Item.method_code:
if (this.getProperty('locked_by_id') !== undefined) {
	aras.AlertError(aras.getResource('', 'fe.assign_package_error'));
	return;
}

var params = {};
params.aras = aras;
params.itemtypeName = 'Team';
params.multiselect = false;
params.packageId = this.getID();
params.type = 'SearchDialog';
var aWindow = aras.getMainWindow() == window.top ? aras.getMainWindow() : window;

aWindow.ArasModules.MaximazableDialog.show('iframe', params).promise.then(function(dlgRes) {
	if (!dlgRes) {
		return;
	}
	assignTeam(dlgRes, this);
	window.res = dlgRes;
});

function assignTeam(res, params) {
	var doc = aras.createXMLDocument();
	doc.loadXML('<body />');
	doc.documentElement.setAttribute('teamId', res.itemID);
	doc.documentElement.setAttribute('packageId', params.packageId);

	var innovator = aras.newIOMInnovator();
	var response = innovator.applyMethod('FE_UpdatePackageAfterAssignTeam', doc.xml);
	if (response.isError()) {
		aras.AlertError(response);
		return;
	}

	if (aras.uiFindWindowEx(params.packageId)) {
		aras.uiReShowItemEx(params.packageId, response.node, 'tab view');
	}
}
